module tijmenvangulik_examples_chartwidgets { //make a name space to prevent mix ups

/*------------------------------------------------------------------------------
    Simple Chart gauge widget example
------------------------------------------------------------------------------*/

    class ExamplePowerGaugeWidget  extends ergometerWidgets.StrokeGaugeWidgetWidget {

		public setBaseOptions(options :HighchartsOptions) {
			super.setBaseOptions(options);
			//options.yAxis.min = 0;
			//options.yAxis.max = 100;
		}

		public strokeDataUpdate(strokeData : pm3.StrokeData) {
			//dummy, override to set value
			var point = this.chart.series[0].points[0];

			point.update(strokeData.power);
		}

		constructor (controller : dashboard.Controller) {
			super(controller);
			this.caption = "Power gauge2";
		}

	}

/*------------------------------------------------------------------------------
    Simple Chart gauge widget example
------------------------------------------------------------------------------*/	
    class ExamplePowerChartWidget  extends ergometerWidgets.HighChartWidget {


		constructor (controller : dashboard.Controller) {
			super(controller);
			this.caption = "Power chart2";

		}


		public getChartOptions() : HighchartsOptions {

			return {
	            chart: {
	                renderTo: 'container',
	                type: 'areaspline'
	            },
	            title: null,
	            xAxis: {
	                title: {
	                    text: ''
	                },
	                labels: {
	                	enabled: false
	                }
	            },
	            yAxis: {
	                title: {
	                    text: ''
	                }                
	            },
	            plotOptions: { 
	            	areaspline: {
		            	animation: false,
		            	marker: {
	                        enabled: false,
	                        states: {
	                            hover: {
	                                enabled: true,
	                                radius: 5
	                            }
	                        }
	                    }
	            	}

	            },
	            tooltip: {
	                formatter: function() {
	                        return '<b>'+ this.series.name +'</b><br/>'+
	                        	    'Nr'+': '+this.x/64+' sec<br/>'+
	                        		'Power: '+Highcharts.numberFormat(this.y, 2);
	                }
	            },
	            legend: {
	                enabled: false
	            },
	            exporting: {
	                enabled: false
	            },
	            series: [{
	                name: 'Power data',
	                data: [ ]
	            }]

	        };
		} 

		public powerCurveUpdate(powerData : pm3.PowerData) {

			var series = this.chart.series[0];
			series.setData([],false);//clear
			for(var  x = 0 ;x < powerData.forceCurveCount ; x++)
				series.addPoint([x, powerData.forceCurve[x]], (x==powerData.forceCurveCount-1) ,false,false);
 		}

	    public loaded() {
          super.loaded();
	      pm3.monitor.pubsubs.subPowerCurveUpdate(this, this.powerCurveUpdate)
        }
        public beforeUnload() {
          super.beforeUnload();
	      pm3.monitor.pubsubs.unsubPowerCurveUpdate(this, this.powerCurveUpdate);
        }

	}
/*------------------------------------------------------------------------------
    Create and remove the widgets in the Plugin
------------------------------------------------------------------------------*/
    class ExamplePlugin extends ExternalPlugin {

        public init() {
            //create the widget(s) on create
            dashboard.widgetRegistry().register(ExamplePowerGaugeWidget,'tijmenvangulik_examples_PowerGaugeWidget','Power gauge2',['Examples']);
            dashboard.widgetRegistry().register(ExamplePowerChartWidget,'tijmenvangulik_examples_PowerChartWidget','Power chart2',['Examples']);
        }
        public remove() {
            //remove the widgets when the plugin is removed
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_PowerGaugeWidget');
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_PowerChartWidget');
        }               
    }

    var plugin : ExamplePlugin;
    plugin = new ExamplePlugin();

}
