            if (!aboveZoom)
                aboveZoom = zoom;
            if (!destZoom)
                destZoom = zoom;
            var parts = 2;
            var called = false;
            function callback(complete) {
                --parts;
                if (called) {
                    return;
                }
                if (parts === 0 || !complete) {
                    called = true;
                    done(complete);
                }
            }
            view.animate({
                center: location,
                duration: duration
            }, callback);
            view.animate({
                zoom: aboveZoom,
                duration: duration / 2
            }, {
                zoom: destZoom,
                duration: duration / 2,
                rotation: ((heading + 90) / 360) * Math.PI * 2
            }, callback);
        };
