            this._map = new ol.Map({
                layers: [mapLayer, this._raceLayer, rowerLayer],
                target: this._$map.get(0),
                view: new ol.View({
                    center: ol.proj.fromLonLat([-0.212708093427223, 51.46670252304421]),
                    zoom: 1,
                    projection: projection
                }),
                controls: []
            });
            if (this._kmlData) {
                this.setRaceLayerFromKmlData();
            }
            //   });
        };
        MapWidgetExample.prototype.resize = function () {
            _super.prototype.resize.call(this);
            this._$map.css({ height: (this.$container.innerHeight() - 24).toString() + "px" });
            if (this._map)
                this._map.updateSize();
        };
        MapWidgetExample.prototype.strokeDataUpdate = function (strokeData) {
            //this._workDistance = strokeData.distance + this._lastWorkTime;
            this._workDistance = strokeData.distance;
            this._speed = strokeData.speed;
            this._lastUpdateTime = new Date().getTime();
            if (!this._finished && this._speed && this._speed > 0) {
                if (!this._animating) {
                    this.startAnimation();
                }
            }
            else
                this.stopAnimation();
        };
        MapWidgetExample.prototype.trainingDataUpdate = function (trainingData) {
            if (this._trainingDistance != trainingData.distance) {
                this._trainingDistance = trainingData.distance;
                //get a new high score for the training distance
                if (trainingData.distance > 0 && this._workDistance == 0 && !this._animating)
                    this.flyToStart(function () { });
            }
            var finished = (trainingData.endDistance > 0 && trainingData.endDuration > 0);
            if (finished != this._finished) {
                this._finished = finished;
                if (this._finished) {
                    this.stopAnimation();
                }
            }
        };
