            var kml = new ol.format.KML();
            var src = new ol.source.Vector();
            var features = kml.readFeatures(this._kmlData, { dataProjection: EPG_KML, featureProjection: EPG_MAP });
            this._mapName = kml.readName(this._kmlData);
            src.addFeatures(features);
            this._raceLayer.setSource(src);
        };
        MapWidgetExample.prototype.loadFromKmlData = function (kmlData) {
            this.setLoadTracEvent();
            this._kmlData = kmlData;
            this.setRaceLayerFromKmlData();
            //var src = this._raceLayer.getSource();
            //src.clear();
            //src.addFeatures(features);
            //this._map.getView().fit(this._raceLayer.getExtent(),this._map.getSize());
        };
