            $('#exampleMapDistance').text(' (' + this.getTrackDistance().toString() + 'm ) ');
            $('#MapExampleZoom').slider("setValue", this._rowingZoom);
            $('#MapExampleZoomValue').text(this._rowingZoom.toString());
            this._dialogMapSettings.open();
        };
