            var newZoom = parseInt($('#MapExampleZoom').val());
            if (newZoom != this._rowingZoom) {
                this._rowingZoom = newZoom;
                if (!this._animating)
                    this.flyToStart(function () { });
            }
            this.saveMapState();
        };
        MapWidgetExample.prototype.getMapSettings = function () {
            return { kmlData: this._kmlData,
                rowingZoom: this._rowingZoom
            };
        };
        MapWidgetExample.prototype.setMapSettings = function (settings) {
            this._kmlData = settings.kmlData;
            this._rowingZoom = settings.rowingZoom || 17;
        };
