            var settingsString = localStorage.getItem("MapExampleWidget_settings");
            var settings;
            if (settingsString != "undefined" && settingsString != null && settingsString != "null") {
                settings = JSON.parse(settingsString);
                this.setMapSettings(settings);
            }
        };
        MapWidgetExample.prototype.getTrackDistance = function () {
            if (this._track && this._track.length > 0)
