module tijmenvangulik_examples_MapWidgetExample { //make a name space to prevent mix ups


    interface MapCoordinate {
        longitude : number;
        latitude : number;
        altitude? : number;
    }
    interface TrackPoint {
        longitude : number;
        latitude : number;
        distance : number;
        cumualtiveDistance  : number;
        heading : number;
    }
    interface MapSettings {
        kmlData : string;
        rowingZoom : number;
    }
    const EPG_MAP = 'EPSG:3857';
    const EPG_KML = 'EPSG:4326';

