        private _raceLayer : ol.layer.Vector;
        private _trackExtent : number[];
        private _rower : ol.Feature;
        private _styles;
        private _kmlData : string = null;
        private _mapName : string;
        private _rowingZoom = 17;
        private _dialogMapSettings : BootstrapDialog;

        public flyToStart(done : ()=>void, duration =2000 ) {
            if ( this._track && this._track.length>0) {
                var trackPoint=this._track[0];
                var currentZoom = this._map.getView().getZoom();
                this.flyTo([trackPoint.longitude,trackPoint.latitude,0 ],this._rowingZoom,
                      trackPoint.heading,currentZoom-1,duration,()=>{
                        this._animatedDistance=0;
                        // start it off
                        this._trackIndex=0;
                        done();                         
                      } );

            }
        }
        public startAnimation() {
            if (!this._animating) {
                this._animating=true;
                var doStart= ()=> {
                    this._animatedDistance=0;
                    this._trackIndex=0;
                    this._map.on('postcompose', this.tickAnimation.bind(this));
                    this._map.render();

                }
                doStart();

                if (this._animatedDistance==0) 
                   doStart();
                 else this.flyToStart( doStart,0); 


                //               
            }


        }

        public stopAnimation() {
            if (this._animating) {
                this._animating=false;
                this._workDistance=0;
                this._trainingDistance=0;
                this._map.un('postcompose', this.tickAnimation);
                this.showWholeMap();
            }
            this._trackIndex=0;
        }

        public tickAnimation(event) {

            if (this._animating) {
                var vectorContext = event.vectorContext;

                var currentTime = new Date().getTime();
                var timeSinceLastUpdate : number =0;
                if (this._lastUpdateTime){
                    timeSinceLastUpdate = currentTime- this._lastUpdateTime;
                }
                var extrapolatedDistance=0;
                if (this._speed && !this._finished) {
                    extrapolatedDistance= (this._speed* (timeSinceLastUpdate/1000));
                }
                var totalDistanceCalc=this._workDistance+extrapolatedDistance;
                //take the next track item when moving past the current
                var currentTrackItem=this._track[this._trackIndex];
                while (this._trackIndex<this._track.length-1 && totalDistanceCalc>= currentTrackItem.cumualtiveDistance+ currentTrackItem.distance) {
                    this._trackIndex++;
                    currentTrackItem=this._track[this._trackIndex];
                }
                var heading=currentTrackItem.heading;
                //calc the relative positon within the current track line
                var relativeDistance= totalDistanceCalc - currentTrackItem.cumualtiveDistance;
                if (relativeDistance<0) //do not go backwards
                    relativeDistance=0;
                //calc the next coordinate
                var dest = this.calcNextCoordinate(currentTrackItem.latitude, currentTrackItem.longitude, relativeDistance,
                    heading);

                this._animatedDistance += relativeDistance;

                var view= this._map.getView();
                var rower_location=ol.proj.fromLonLat([dest.longitude,dest.latitude,0 ]);

                view.setCenter(rower_location);
                var rowerPoint= new ol.geom.Point(rower_location);
                this._rower.setGeometry(rowerPoint);
                //vectorContext.drawFeature(this._rower, this._styles.rower);
                //var feature = new ol.Feature(rower_location);
                //vectorContext.drawFeature(feature, this._styles.rower);

                console.log({
                  center:[dest.longitude,dest.latitude,0 ],
                  duration: 10,
                  rotation: heading
                });
                this._map.render();
            }

        }

        /* Helper functions, courtesy of
         http://www.movable-type.co.uk/scripts/latlong.html */
        public calcDistance(lat1 : number, lng1 : number, lat2 : number, lng2 : number) {
            var a = Math.sin(lat1 * Math.PI / 180) * Math.sin(lat2 * Math.PI / 180);
            var b = Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
                Math.cos((lng2 - lng1) * Math.PI / 180);
            return 6371000 * Math.acos(a + b);
        }

        public radians(n) {
            return n * (Math.PI / 180);
        }
        public degrees(n) {
            return n * (180 / Math.PI);
        }

        public calcHeading(startLat,startLong,endLat,endLong){
            startLat = this.radians(startLat);
            startLong = this.radians(startLong);
            endLat = this.radians(endLat);
            endLong = this.radians(endLong);

            var dLong = endLong - startLong;

            var dPhi = Math.log(Math.tan(endLat/2.0+Math.PI/4.0)/Math.tan(startLat/2.0+Math.PI/4.0));
            if (Math.abs(dLong) > Math.PI){
                if (dLong > 0.0)
                    dLong = -(2.0 * Math.PI - dLong);
                else
                    dLong = (2.0 * Math.PI + dLong);
            }

            return (this.degrees(Math.atan2(dLong, dPhi)) + 360.0) % 360.0;
        }

        public calcNextCoordinate(lat : number, lng : number, dist : number, heading : number) : MapCoordinate {
            lat *= Math.PI / 180;
            lng *= Math.PI / 180;
            heading *= Math.PI / 180;
            dist /= 6371000; // angular dist

            var lat2 = Math.asin(Math.sin(lat) * Math.cos(dist) +
                Math.cos(lat) * Math.sin(dist) * Math.cos(heading));

            return {
                latitude: 180 / Math.PI * lat2,
                longitude: 180 / Math.PI *
                (lng + Math.atan2(Math.sin(heading) * Math.sin(dist) * Math.cos(lat2),
                    Math.cos(dist) - Math.sin(lat) * Math.sin(lat2)))};
        }

        public initControl() {
            super.initControl();
