        public setLoadTracEvent() {
          var self=this;
          this._raceLayer.once('change', function(evt) {
              try {
                  var src=this.getSource();
                  if (src.getState() == 'ready') {

                    var features=src.getFeatures();
                    if (typeof features === 'undefined' || !features || features.length==0) 
                       throw 'No features in KML file';
                    var geometry=features[0].getGeometry();
                    if (typeof geometry === 'undefined' || !geometry || geometry.length==0)
                      throw 'No geometry found in KML file';
                    var coordinates=geometry.getCoordinates();
                    if (typeof coordinates === 'undefined' || !coordinates || coordinates.length==0)
                      throw 'No coordinates found in KML file';
                    self._trackExtent=geometry.getExtent();
                    self._track=[];
                    var cumualtiveDistance=0;
                    for (var i=0;i<coordinates.length-2;i++ ) {

                        var srcCoor =ol.proj.toLonLat(coordinates[i]);
                        var nextCoor =ol.proj.toLonLat(coordinates[i+1]);

                        var distance= self.calcDistance(srcCoor[1],srcCoor[0],nextCoor[1],nextCoor[0]);

                        var heading=self.calcHeading(srcCoor[1],srcCoor[0],nextCoor[1],nextCoor[0]);

                        self._track.push({
                            longitude : srcCoor[0],
                            latitude : srcCoor[1],
                            distance : distance,
                            cumualtiveDistance: cumualtiveDistance,
                            heading: heading
                        });
                        cumualtiveDistance += distance; 
                    }
                    if (self._track.length>0)
                       self.trackLoaded();
                  }
               }
               catch (e) {
                   this._kmlData=null;
                   app.showTopError(e);
               }
            });            
        }
