        public strokeDataUpdate(strokeData : pm3.StrokeData) {
            //this._workDistance = strokeData.distance + this._lastWorkTime;
            this._workDistance = strokeData.distance;
            this._speed=strokeData.speed;
            this._lastUpdateTime = new Date().getTime();
            if (!this._finished && this._speed && this._speed>0) {
                if (!this._animating) {
                    this.startAnimation();
                }
            }
            else this.stopAnimation();

        }

        public trainingDataUpdate(trainingData : pm3.TrainingData) {

            if (this._trainingDistance!=trainingData.distance) {
                this._trainingDistance=trainingData.distance;
                //get a new high score for the training distance
                if (trainingData.distance >0 && this._workDistance==0 && !this._animating)
                    this.flyToStart( ()=>{});

            }
            var finished=(trainingData.endDistance>0 && trainingData.endDuration>0);
            if (finished!=this._finished) {
                this._finished=finished;
                if (this._finished) {
                    this.stopAnimation();
                    //this._lastWorkTime= this._lastWorkTime+ trainingData.endDistance;
                }
            }

        }
