module tijmenvangulik_examples_RaceWidgets { //make a name space to prevent mix ups

/*------------------------------------------------------------------------------
    Simple Chart gauge widget example
------------------------------------------------------------------------------*/

    class ExampleRaceTrackWidget  extends ergometerWidgets.CanvasWidget {
        private _trainingDistance : number;
        private _workDistance : number;
        private _updateDelay : number; 
        private _animating : boolean;
        private _waitingForTimeout : boolean;
        private _speed : number;
        private _lastUpdateTime : number;
        private _finished;

        public resize() {
			super.resize();
			this.paint();

		}

        public strokeDataUpdate(strokeData : pm3.StrokeData) {
            this._workDistance = strokeData.distance;

            this._speed=strokeData.speed;
            this._lastUpdateTime = new Date().getTime();
            if (!this._finished && this._speed && this._speed>0) {
                if (!this._animating) {
                    this._animating=true;
                    this.doAnimation();
                }
            }
            else this._animating=false;

        }

        public trainingDataUpdate(trainingData : pm3.TrainingData) {
            if (this._trainingDistance!=trainingData.distance) {
				this._trainingDistance=trainingData.distance;
                //get a new high score for the training distance
                this.paint();
			}
			this._finished=  (trainingData.endDistance>0 && trainingData.endDuration>0);
			if (this._finished) {
			    this._speed=0;
			    this._animating=false;
			}
        }
        public loaded() {
          super.loaded();
          pm3.monitor.pubsubs.subStrokeDataUpdate(this, this.strokeDataUpdate);
	      pm3.monitor.pubsubs.subTrainingDataUpdate(this, this.trainingDataUpdate);
        }
        public beforeUnload() {
          super.beforeUnload();
          pm3.monitor.pubsubs.unsubStrokeDataUpdate(this.strokeDataUpdate);
	      pm3.monitor.pubsubs.unsubTrainingDataUpdate(this.trainingDataUpdate);
        }

	    public paint() {
	        if (this.isLoaded) {

	        	var canvas= this.canvas;
		        var context=canvas.getContext("2d");

                //clean background 
		        context.lineWidth=4;
				context.strokeStyle="#4572A7"
		        context.fillStyle="#FFFFFF";
		        context.rect(0,0,canvas.width,canvas.height);
		        context.fill();

                var radius = 25;
                var radiusx = radius;
                var circleOffsetY =20;
                var width = canvas.width-radiusx*2;
				//var maxDistance= rower.trainingDistance;
				context.fillStyle="#CCCCCC";
                context.strokeStyle="#CCCCCC";
                var currentTime = new Date().getTime();
                var timeSinceLastUpdate : number =0;
                if (this._lastUpdateTime){
                    timeSinceLastUpdate = currentTime- this._lastUpdateTime;
                }
                var extrapolatedDistance=0;
                if (this._speed && !this._finished) {
                    extrapolatedDistance= (this._speed* (timeSinceLastUpdate/1000));  
                }


				var relativePosition = width * ((this._workDistance+extrapolatedDistance)/ this._trainingDistance) ;
				var ellipseX =  relativePosition+radiusx;
                var ellipseY = radius+circleOffsetY;
                utilities.ellipse(context,
                    ellipseX,ellipseY,
                    radiusx, radius
                );
                context.fill();

	        }
        }

        public doAnimation() {

            if (this.isLoaded && this._animating) {
                this.paint();
                if (this._animating && !this._waitingForTimeout) {
                    this._waitingForTimeout = true;
                    //use animation frames
                    utilities.requestTimeout(
                        () => {
                            this._waitingForTimeout = false;
                            this.doAnimation();
                        },this._updateDelay);
                }
            }
        }
        constructor (controller : dashboard.Controller) {
            super(controller);
            this._trainingDistance=0;
            this._workDistance=0;
            this._updateDelay = 1000/20; //20 frames per second
            this._animating =false;
            this._waitingForTimeout =false;
            this._speed=0;
            this._finished=false;
			this.caption = "Race widget example";

		}

	}
/*------------------------------------------------------------------------------
    Create and remove the widgets in the Plugin
------------------------------------------------------------------------------*/
    class ExamplePlugin extends ExternalPlugin {

        public raceTrackWidget : ExampleRaceTrackWidget;


        public init() {
            //create the widget(s) on create
            dashboard.widgetRegistry().register(ExampleRaceTrackWidget,'tijmenvangulik_examples_RaceWidgetExample','Race',['Examples']);
        }
        public remove() {
            //remove the widgets when the plugin is removed
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_RaceWidgetExample');
        }               
    }

    var plugin : ExamplePlugin;
    plugin = new ExamplePlugin();

}
