module tijmenvangulik_examples_valuewidgets { //make a name space to prevent mix ups

/*------------------------------------------------------------------------------
    Stroke phase value widget example
------------------------------------------------------------------------------*/
    class ExamplePhaseValueWidget extends ergometerWidgets.StrokeValueWidget {

        public strokePhaseUpdate(phase : pm3.StrokePhase, duration : number) {
			this.valueText = pm3.StrokePhaseText[phase];
		}

        public loaded() {
          super.loaded();
          pm3.monitor.pubsubs.subNewStrokePhase(this, this.strokePhaseUpdate);
        }
        public beforeUnload() {
          super.beforeUnload();
          pm3.monitor.pubsubs.unsubNewStrokePhase(this.strokePhaseUpdate);
        }

		public initControl() {
            super.initControl();
        	this.labelText = "Phase2"; //label before value
		}
		constructor (controller : dashboard.Controller) {
        	super(controller);
        	this.caption = "Phase2"; //menu item or window caption    
		}    
    }
/*------------------------------------------------------------------------------
    Stroke power widget
    note the base class  StrokeValueWidget connects the strokeDataUpdate to the event using
    pm3.monitor.pubsubs.subStrokeDataUpdate(this, this.strokeDataUpdate);
    see the ExamplePhaseValueWidget widget how to attach to events
------------------------------------------------------------------------------*/
    class ExampleStrokeValuePowerWidget  extends ergometerWidgets.StrokeValueWidget {

		public strokeDataUpdate(strokeData : pm3.StrokeData) {
			this.valueText = strokeData.power.toString();
		}

		public initControl() {
			super.initControl();
 			this.labelText = "Power";

		}
		constructor (controller : dashboard.Controller) {
			super(controller);
			this.caption = "Power2";//no caption needed
		}
	} 

/*------------------------------------------------------------------------------
    training data widget (or workout)
    note the base class  StrokeValueWidget connects the trainingDataUpdate to the event using
      pm3.monitor.pubsubs.subTrainingDataUpdate(this, this.trainingDataUpdate);
    see the ExamplePhaseValueWidget widget how to attach to events
------------------------------------------------------------------------------*/
    class ExampleTrainingDataWidget  extends ergometerWidgets.TrainingValueWidget {

		public trainingDataUpdate(trainingData : pm3.TrainingData) {
			if (trainingData.workoutType)
				this.valueText =pm3.WorkoutTypeText[trainingData.workoutType]
			else this.valueText = '';

			switch (trainingData.workoutType ) {
				case pm3.WorkoutType.FixedDistanceNoSplits :
				case pm3.WorkoutType.FixtedDistanceSplits :
				case pm3.WorkoutType.FixedDistanceInterval :
				  	this.valueText=this.valueText+' '+trainingData.distance.toString()+'m';
				  	break;
				case pm3.WorkoutType.FixedTimeNoSplits :
				case pm3.WorkoutType.FixedTimeSplits :
				case pm3.WorkoutType.FixedTimeInterval :
				  	this.valueText=this.valueText+' '+ utilities.formatRelativeTime(trainingData.duration);
				  	break;
			}
			//skipp intervalType
			if (trainingData.workoutType==pm3.WorkoutType.FixedTimeInterval ||
				trainingData.workoutType==pm3.WorkoutType.FixedDistanceInterval || 
				trainingData.workoutType==pm3.WorkoutType.VariableInterval) {
				this.valueText= this.valueText+' x'+trainingData.workoutIntervalCount.toString()+
								'rest '+utilities.formatRelativeTime(trainingData.restTime);
			}
			if (trainingData.workoutState)
				this.valueText= this.valueText+' '+pm3.WorkoutStateText[trainingData.workoutState] 
			else this.valueText='';

		}

		public initControl() {
			super.initControl();
			this.labelText = "Workout";

		}
		constructor (controller : dashboard.Controller) {
            super(controller);
			this.caption = "Workout2";
		}
	}

/*------------------------------------------------------------------------------
    Create and remove the widgets in the Plugin
------------------------------------------------------------------------------*/
    class ExamplePlugin extends ExternalPlugin {

        public init() {
            //create the widget(s) on create
            dashboard.widgetRegistry().register(ExamplePhaseValueWidget,'tijmenvangulik_examples_StrokePhaseWidget','Phase',['Examples']);
            dashboard.widgetRegistry().register(ExampleStrokeValuePowerWidget,'tijmenvangulik_examples_StrokeValuePowerWidget','Power',['Examples']);
            dashboard.widgetRegistry().register(ExampleTrainingDataWidget,'tijmenvangulik_examples_TrainingDataWidget','Workout',['Examples']);

        }
        public remove() {
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_StrokePhaseWidget');
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_StrokeValuePowerWidget');
            dashboard.widgetRegistry().deregister('tijmenvangulik_examples_TrainingDataWidget');
        }               
    }

    var plugin : ExamplePlugin;
    plugin = new ExamplePlugin();

}
